mapper.select:
select 

from t_dev_relation
where device_id = #{deviceId,jdbcType=VARCHAR}
  and device_id_child = #{deviceIdChild,jdbcType=VARCHAR}

mapper.delete:
delete from t_dev_relation
where device_id = #{deviceId,jdbcType=VARCHAR}
  and device_id_child = #{deviceIdChild,jdbcType=VARCHAR}

mapper.insert:
insert into t_dev_relation (device_id, device_id_child, gate
  )
values (#{deviceId,jdbcType=VARCHAR}, #{deviceIdChild,jdbcType=VARCHAR}, #{gate,jdbcType=INTEGER}
  )

mapper.update:
update t_dev_relation


    gate = #{gate,jdbcType=INTEGER},


where device_id = #{deviceId,jdbcType=VARCHAR}
  and device_id_child = #{deviceIdChild,jdbcType=VARCHAR}

update t_dev_relation
set gate = #{gate,jdbcType=INTEGER}
where device_id = #{deviceId,jdbcType=VARCHAR}
  and device_id_child = #{deviceIdChild,jdbcType=VARCHAR}
