mapper.select:
select

from t_dev_consume
where client_id = #{clientId,jdbcType=VARCHAR}
and
device_id = #{deviceId,jdbcType=VARCHAR}
and create_time =
#{createTime,jdbcType=TIMESTAMP}

select

from t_dev_consume dc where dc.device_id =#{deviceId,jdbcType=VARCHAR}
and dc.status='1' order by dc.create_time DESC LIMIT 1

mapper.delete:
delete from
t_dev_consume
where client_id = #{clientId,jdbcType=VARCHAR}
and
device_id = #{deviceId,jdbcType=VARCHAR}
and create_time =
#{createTime,jdbcType=TIMESTAMP}
